import React, { useState, useEffect } from 'react';
import { getCurrentUser } from '../services/firebase';
import CompletionModal from './CompletionModal';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchTasks = async () => {
    try {
      const user = getCurrentUser();
      if (!user) return;

      const response = await fetch(`${process.env.REACT_APP_API_URL}/work`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        // Filter only pending tasks
        const pendingTasks = data.filter(task => task.status === 'pending');
        setTasks(pendingTasks);
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleClick = (task) => {
    setSelectedTask(task);
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const refreshTasks = () => {
    fetchTasks();
  };

  const formatDeadline = (deadline) => {
    const date = new Date(deadline);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    let urgencyClass = '';
    let urgencyText = '';
    
    if (diffDays < 0) {
      urgencyClass = 'text-danger';
      urgencyText = ' (OVERDUE)';
    } else if (diffDays <= 1) {
      urgencyClass = 'text-warning';
      urgencyText = ' (DUE SOON)';
    }
    
    return {
      formatted: date.toLocaleDateString() + ' at ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}),
      urgencyClass,
      urgencyText
    };
  };

  if (loading) {
    return (
      <div className="p-3">
        <h2>Your To-Do List</h2>
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <div className="mt-2">Loading tasks...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-3">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Your To-Do List</h2>
        <button 
          className="btn btn-outline-primary btn-sm" 
          onClick={fetchTasks}
          disabled={loading}
        >
          Refresh
        </button>
      </div>
      
      {tasks.length === 0 ? (
        <div className="alert alert-info">
          <i className="bi bi-info-circle me-2"></i>
          No pending tasks assigned to you. Great job staying on top of your work!
        </div>
      ) : (
        <div className="row">
          <div className="col-12">
            <div className="mb-3">
              <small className="text-muted">You have {tasks.length} pending task{tasks.length !== 1 ? 's' : ''}</small>
            </div>
            <div className="list-group">
              {tasks.map(task => {
                const deadlineInfo = formatDeadline(task.deadline);
                return (
                  <div key={task._id} className="list-group-item">
                    <div className="d-flex justify-content-between align-items-start">
                      <div className="flex-grow-1">
                        <h5 className="mb-2">{task.task}</h5>
                        <div className={`mb-2 ${deadlineInfo.urgencyClass}`}>
                          <i className="bi bi-calendar-event me-1"></i>
                          <strong>Deadline:</strong> {deadlineInfo.formatted}{deadlineInfo.urgencyText}
                        </div>
                        {task.instructions && (
                          <div className="mb-2">
                            <strong>Instructions:</strong>
                            <div className="mt-1 p-2 bg-light rounded">
                              {task.instructions}
                            </div>
                          </div>
                        )}
                        {task.assignedBy && (
                          <div className="text-muted">
                            <small>Assigned by: {task.assignedBy.name}</small>
                          </div>
                        )}
                      </div>
                      <div className="ms-3">
                        <button 
                          className="btn btn-primary" 
                          onClick={() => handleClick(task)}
                        >
                          <i className="bi bi-check-circle me-1"></i>
                          Mark Complete
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      )}
      {selectedTask && (
        <CompletionModal 
          task={selectedTask} 
          onClose={handleCloseModal} 
          onUpdate={refreshTasks} 
        />
      )}
    </div>
  );
};

export default TodoList;
