import React, { useState, createContext, useContext } from 'react';
import { 
  Users, 
  Home, 
  Plus, 
  Send, 
  CheckCircle, 
  Circle,
  LogOut,
  Calendar,
  Clock,
  User,
  AlertCircle,
  X
} from 'lucide-react';

// Mock database simulation
const mockDB = {
  users: [
    { id: 1, username: 'admin', password: 'admin123', role: 'admin', name: 'Admin User', email: 'admin@taskpilot.com' },
    { id: 2, username: 'john_worker', password: 'worker123', role: 'worker', name: 'John Doe', email: 'john@company.com' },
    { id: 3, username: 'jane_worker', password: 'worker123', role: 'worker', name: 'Jane Smith', email: 'jane@company.com' }
  ],
  tasks: [
    {
      id: 1,
      workerId: 2,
      title: 'Complete Website Design',
      description: 'Design the new company website homepage',
      instructions: 'Use the brand colors and follow the style guide provided',
      dueDate: '2025-09-05',
      status: 'pending',
      assignedBy: 1,
      createdAt: '2025-09-01T09:00:00Z',
      completedAt: null,
      workerNotes: '',
      workLink: ''
    }
  ]
};

// Auth Context
const AuthContext = createContext();

const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Auth Provider
const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState(mockDB.users);
  const [tasks, setTasks] = useState(mockDB.tasks);

  const login = (username, password) => {
    const foundUser = users.find(u => u.username === username && u.password === password);
    if (foundUser) {
      setUser(foundUser);
      return { success: true };
    }
    return { success: false, error: 'Invalid credentials' };
  };

  const logout = () => {
    setUser(null);
  };

  const createUser = (userData) => {
    const newUser = {
      id: users.length + 1,
      ...userData,
      role: 'worker'
    };
    setUsers([...users, newUser]);
    return newUser;
  };

  const assignTask = (taskData) => {
    const newTask = {
      id: tasks.length + 1,
      ...taskData,
      status: 'pending',
      assignedBy: user.id,
      createdAt: new Date().toISOString(),
      completedAt: null,
      workerNotes: '',
      workLink: ''
    };
    setTasks([...tasks, newTask]);

    // Mock email notification
    console.log(`Email sent to worker: You have new work assigned!`);
    return newTask;
  };

  const completeTask = (taskId, completionData) => {
    setTasks(tasks.map(task => 
      task.id === taskId 
        ? { 
            ...task, 
            status: 'completed',
            completedAt: new Date().toISOString(),
            ...completionData
          }
        : task
    ));
  };

  const getWorkers = () => users.filter(u => u.role === 'worker');
  const getUserTasks = (userId) => tasks.filter(t => t.workerId === userId);

  return (
    <AuthContext.Provider value={{
      user,
      users,
      tasks,
      login,
      logout,
      createUser,
      assignTask,
      completeTask,
      getWorkers,
      getUserTasks
    }}>
      {children}
    </AuthContext.Provider>
  );
};

// Login Component
const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth();

  const handleSubmit = (e) => {
    e.preventDefault();
    const result = login(username, password);
    if (!result.success) {
      setError(result.error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-purple-900 flex items-center justify-center p-4">
      <div className="bg-white rounded-xl shadow-2xl p-8 w-full max-w-md">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Task Pilot</h1>
          <p className="text-gray-600">Sign in to your account</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Username
            </label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Enter your username"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Password
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Enter your password"
              required
            />
          </div>

          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-3 flex items-center text-red-700">
              <AlertCircle className="h-4 w-4 mr-2" />
              {error}
            </div>
          )}

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg hover:bg-blue-700 transition duration-200 font-medium"
          >
            Sign In
          </button>
        </form>

        <div className="mt-6 text-center text-sm text-gray-600">
          <p>Demo Credentials:</p>
          <p>Admin: admin / admin123</p>
          <p>Worker: john_worker / worker123</p>
        </div>
      </div>
    </div>
  );
};

// Create User Modal
const CreateUserModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    email: ''
  });
  const { createUser } = useAuth();

  const handleSubmit = (e) => {
    e.preventDefault();
    createUser(formData);
    setFormData({ username: '', password: '', name: '', email: '' });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl p-6 w-full max-w-md">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-800">Create New Worker</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X className="h-6 w-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Full Name
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Username
            </label>
            <input
              type="text"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Password
            </label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Email
            </label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Create User
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Task Assignment Modal
const TaskAssignmentModal = ({ isOpen, onClose, worker }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    instructions: '',
    dueDate: ''
  });
  const { assignTask } = useAuth();

  const handleSubmit = (e) => {
    e.preventDefault();
    assignTask({
      ...formData,
      workerId: worker.id
    });
    setFormData({ title: '', description: '', instructions: '', dueDate: '' });
    onClose();
  };

  if (!isOpen || !worker) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl p-6 w-full max-w-lg">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-800">Assign Task to {worker.name}</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X className="h-6 w-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Task Title
            </label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              placeholder="What needs to be done?"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              rows="3"
              placeholder="Describe the task..."
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Instructions
            </label>
            <textarea
              value={formData.instructions}
              onChange={(e) => setFormData({...formData, instructions: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              rows="3"
              placeholder="How should it be done?"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Due Date
            </label>
            <input
              type="date"
              value={formData.dueDate}
              onChange={(e) => setFormData({...formData, dueDate: e.target.value})}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center justify-center"
            >
              <Send className="h-4 w-4 mr-2" />
              Send Task
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Task Completion Modal
const TaskCompletionModal = ({ isOpen, onClose, task }) => {
  const [completionType, setCompletionType] = useState('as_instructed');
  const [notes, setNotes] = useState('');
  const [workLink, setWorkLink] = useState('');
  const { completeTask } = useAuth();

  const handleSubmit = (e) => {
    e.preventDefault();
    completeTask(task.id, {
      workerNotes: completionType === 'differently' ? notes : '',
      workLink: workLink
    });
    setNotes('');
    setWorkLink('');
    setCompletionType('as_instructed');
    onClose();
  };

  if (!isOpen || !task) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl p-6 w-full max-w-lg">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-800">Complete Task</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X className="h-6 w-6" />
          </button>
        </div>

        <div className="mb-4 p-3 bg-gray-50 rounded-lg">
          <h3 className="font-medium text-gray-800">{task.title}</h3>
          <p className="text-sm text-gray-600 mt-1">{task.description}</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              How did you complete this task?
            </label>
            <div className="space-y-2">
              <label className="flex items-center">
                <input
                  type="radio"
                  value="as_instructed"
                  checked={completionType === 'as_instructed'}
                  onChange={(e) => setCompletionType(e.target.value)}
                  className="mr-2"
                />
                <span>As instructed by admin</span>
              </label>
              <label className="flex items-center">
                <input
                  type="radio"
                  value="differently"
                  checked={completionType === 'differently'}
                  onChange={(e) => setCompletionType(e.target.value)}
                  className="mr-2"
                />
                <span>Differently than instructed</span>
              </label>
            </div>
          </div>

          {completionType === 'differently' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Explain how you did it
              </label>
              <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                rows="3"
                placeholder="Explain why you did it differently and how..."
                required
              />
            </div>
          )}

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Work Link (optional)
            </label>
            <input
              type="url"
              value={workLink}
              onChange={(e) => setWorkLink(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              placeholder="https://..."
            />
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="flex-1 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center justify-center"
            >
              <CheckCircle className="h-4 w-4 mr-2" />
              Mark Complete
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Admin Dashboard
const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('home');
  const [showCreateUser, setShowCreateUser] = useState(false);
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const { user, logout, getWorkers, tasks } = useAuth();

  const workers = getWorkers();

  const handleWorkerClick = (worker) => {
    setSelectedWorker(worker);
    setShowTaskModal(true);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-lg">
        <div className="p-6 border-b">
          <h1 className="text-xl font-bold text-gray-800">Task Pilot</h1>
          <p className="text-sm text-gray-600">Admin Panel</p>
        </div>

        <nav className="mt-6">
          <button
            onClick={() => setActiveTab('home')}
            className={`w-full flex items-center px-6 py-3 text-left hover:bg-gray-50 ${
              activeTab === 'home' ? 'bg-blue-50 text-blue-600 border-r-2 border-blue-600' : 'text-gray-700'
            }`}
          >
            <Home className="h-5 w-5 mr-3" />
            Home
          </button>

          <button
            onClick={() => setActiveTab('create-user')}
            className={`w-full flex items-center px-6 py-3 text-left hover:bg-gray-50 ${
              activeTab === 'create-user' ? 'bg-blue-50 text-blue-600 border-r-2 border-blue-600' : 'text-gray-700'
            }`}
          >
            <Plus className="h-5 w-5 mr-3" />
            Create New User
          </button>
        </nav>

        <div className="absolute bottom-0 w-64 p-6 border-t">
          <div className="flex items-center mb-3">
            <User className="h-8 w-8 text-gray-400 mr-3" />
            <div>
              <p className="font-medium text-gray-800">{user.name}</p>
              <p className="text-sm text-gray-500">Administrator</p>
            </div>
          </div>
          <button
            onClick={logout}
            className="flex items-center text-red-600 hover:text-red-700"
          >
            <LogOut className="h-4 w-4 mr-2" />
            Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        {activeTab === 'home' && (
          <div className="p-8">
            <div className="mb-8">
              <h2 className="text-2xl font-bold text-gray-800 mb-2">Workers</h2>
              <p className="text-gray-600">Click on a worker to assign them a task</p>
            </div>

            <div className="grid gap-4">
              {workers.map((worker) => {
                const workerTasks = tasks.filter(t => t.workerId === worker.id);
                const pendingTasks = workerTasks.filter(t => t.status === 'pending').length;
                const completedTasks = workerTasks.filter(t => t.status === 'completed').length;

                return (
                  <div
                    key={worker.id}
                    className="bg-white rounded-lg shadow-sm border hover:shadow-md transition-shadow cursor-pointer"
                    onClick={() => handleWorkerClick(worker)}
                  >
                    <div className="p-6">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          <User className="h-10 w-10 text-gray-400 mr-4" />
                          <div>
                            <h3 className="font-semibold text-gray-800">{worker.name}</h3>
                            <p className="text-sm text-gray-500">{worker.email}</p>
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="flex space-x-4">
                            <div className="text-center">
                              <div className="text-lg font-semibold text-orange-600">{pendingTasks}</div>
                              <div className="text-xs text-gray-500">Pending</div>
                            </div>
                            <div className="text-center">
                              <div className="text-lg font-semibold text-green-600">{completedTasks}</div>
                              <div className="text-xs text-gray-500">Completed</div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}

              {workers.length === 0 && (
                <div className="text-center py-12">
                  <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-800 mb-2">No workers yet</h3>
                  <p className="text-gray-500 mb-4">Create your first worker to get started</p>
                  <button
                    onClick={() => setActiveTab('create-user')}
                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                  >
                    Create First Worker
                  </button>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'create-user' && (
          <div className="p-8">
            <div className="max-w-md">
              <h2 className="text-2xl font-bold text-gray-800 mb-6">Create New Worker</h2>
              <button
                onClick={() => setShowCreateUser(true)}
                className="w-full flex items-center justify-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                <Plus className="h-5 w-5 mr-2" />
                Add New Worker
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Modals */}
      <CreateUserModal 
        isOpen={showCreateUser} 
        onClose={() => setShowCreateUser(false)} 
      />

      <TaskAssignmentModal
        isOpen={showTaskModal}
        onClose={() => {
          setShowTaskModal(false);
          setSelectedWorker(null);
        }}
        worker={selectedWorker}
      />
    </div>
  );
};

// Worker Dashboard
const WorkerDashboard = () => {
  const [showCompletionModal, setShowCompletionModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const { user, logout, getUserTasks } = useAuth();

  const userTasks = getUserTasks(user.id);

  const handleTaskClick = (task) => {
    if (task.status === 'pending') {
      setSelectedTask(task);
      setShowCompletionModal(true);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="px-6 py-4 flex justify-between items-center">
          <div>
            <h1 className="text-xl font-bold text-gray-800">Task Pilot</h1>
            <p className="text-sm text-gray-600">Welcome back, {user.name}</p>
          </div>
          <button
            onClick={logout}
            className="flex items-center text-red-600 hover:text-red-700"
          >
            <LogOut className="h-4 w-4 mr-2" />
            Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="p-6">
        <div className="max-w-4xl mx-auto">
          <div className="mb-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-2">Your Tasks</h2>
            <p className="text-gray-600">Click on a pending task to mark it as complete</p>
          </div>

          <div className="bg-white rounded-lg shadow-sm">
            {userTasks.length === 0 ? (
              <div className="p-8 text-center">
                <CheckCircle className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-800 mb-2">No tasks assigned</h3>
                <p className="text-gray-500">You'll see your tasks here when they're assigned</p>
              </div>
            ) : (
              <div className="divide-y">
                {userTasks.map((task) => (
                  <div
                    key={task.id}
                    className={`p-4 hover:bg-gray-50 transition-colors ${
                      task.status === 'pending' ? 'cursor-pointer' : ''
                    }`}
                    onClick={() => handleTaskClick(task)}
                  >
                    <div className="flex items-start space-x-3">
                      <div className="flex-shrink-0 mt-1">
                        {task.status === 'pending' ? (
                          <Circle className="h-5 w-5 text-gray-400" />
                        ) : (
                          <CheckCircle className="h-5 w-5 text-green-600" />
                        )}
                      </div>

                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between">
                          <h3 className={`font-medium ${
                            task.status === 'completed' ? 'line-through text-gray-500' : 'text-gray-800'
                          }`}>
                            {task.title}
                          </h3>
                          <div className="flex items-center space-x-2 text-sm text-gray-500">
                            <Calendar className="h-4 w-4" />
                            <span>{new Date(task.dueDate).toLocaleDateString()}</span>
                          </div>
                        </div>

                        <p className="text-gray-600 mt-1">{task.description}</p>

                        <div className="mt-2">
                          <p className="text-sm text-gray-500">
                            <strong>Instructions:</strong> {task.instructions}
                          </p>
                        </div>

                        {task.status === 'completed' && (
                          <div className="mt-3 p-3 bg-green-50 rounded-lg">
                            <div className="flex items-center text-sm text-green-700 mb-2">
                              <CheckCircle className="h-4 w-4 mr-1" />
                              Completed on {new Date(task.completedAt).toLocaleString()}
                            </div>
                            {task.workerNotes && (
                              <p className="text-sm text-gray-600 mb-1">
                                <strong>Notes:</strong> {task.workerNotes}
                              </p>
                            )}
                            {task.workLink && (
                              <p className="text-sm">
                                <strong>Work Link:</strong>{' '}
                                <a
                                  href={task.workLink}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="text-blue-600 hover:underline"
                                >
                                  {task.workLink}
                                </a>
                              </p>
                            )}
                          </div>
                        )}

                        {task.status === 'pending' && (
                          <div className="mt-2 flex items-center text-sm text-orange-600">
                            <Clock className="h-4 w-4 mr-1" />
                            Click to complete this task
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Task Completion Modal */}
      <TaskCompletionModal
        isOpen={showCompletionModal}
        onClose={() => {
          setShowCompletionModal(false);
          setSelectedTask(null);
        }}
        task={selectedTask}
      />
    </div>
  );
};

// Main App Component
const App = () => {
  const { user } = useAuth();

  if (!user) {
    return <Login />;
  }

  return user.role === 'admin' ? <AdminDashboard /> : <WorkerDashboard />;
};

// Root App with Provider
const TaskPilotApp = () => {
  return (
    <AuthProvider>
      <App />
    </AuthProvider>
  );
};

export default TaskPilotApp;
