import React, { useState } from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const CompletionModal = ({ task, onClose, onUpdate }) => {
  const [explanation, setExplanation] = useState('');
  const [workLink, setWorkLink] = useState('');
  const [status, setStatus] = useState('done');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/work/complete/${task.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          status,
          explanation: explanation || undefined,
          workLink: workLink || undefined
        })
      });

      if (response.ok) {
        onUpdate();
        onClose();
        alert('Task completed successfully!');
      } else {
        const error = await response.json();
        alert('Error completing task: ' + error.message);
      }
    } catch (error) {
      console.error('Error completing task:', error);
      alert('Error completing task: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const modalStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      width: '500px',
      maxWidth: '90%',
      maxHeight: '80%',
      overflow: 'auto'
    },
    overlay: {
      backgroundColor: 'rgba(0, 0, 0, 0.5)'
    }
  };

  return (
    <Modal isOpen={true} onRequestClose={onClose} style={modalStyles}>
      <div>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h3 className="mb-0">Complete Task</h3>
          <button 
            type="button" 
            className="btn-close" 
            onClick={onClose}
            disabled={loading}
          ></button>
        </div>
        
        <div className="mb-3 p-3 bg-light rounded">
          <div className="mb-2">
            <strong>Task:</strong> {task.task}
          </div>
          {task.instructions && (
            <div className="mb-2">
              <strong>Instructions:</strong> {task.instructions}
            </div>
          )}
          <div>
            <strong>Deadline:</strong> {new Date(task.deadline).toLocaleDateString()} at {new Date(task.deadline).toLocaleTimeString()}
          </div>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Status:</label>
            <select 
              className="form-control" 
              value={status} 
              onChange={e => setStatus(e.target.value)}
              disabled={loading}
            >
              <option value="done">Done as requested</option>
              <option value="custom">Done differently (explain below)</option>
            </select>
          </div>
          
          <div className="mb-3">
            <label className="form-label">
              {status === 'custom' ? 'Explanation (required - how did you complete it differently):' : 'Additional notes (optional):'}
            </label>
            <textarea 
              className="form-control" 
              rows="4"
              value={explanation} 
              onChange={e => setExplanation(e.target.value)}
              placeholder={status === 'custom' ? 
                "Explain how you completed the task differently from the instructions..." : 
                "Any additional notes about completion..."
              }
              required={status === 'custom'}
              disabled={loading}
            />
          </div>
          
          <div className="mb-3">
            <label className="form-label">Link to your work (optional):</label>
            <input 
              type="url" 
              className="form-control" 
              value={workLink} 
              onChange={e => setWorkLink(e.target.value)}
              placeholder="https://... (link to your completed work)"
              disabled={loading}
            />
          </div>
          
          <div className="d-flex gap-2 justify-content-end">
            <button 
              type="button" 
              className="btn btn-secondary" 
              onClick={onClose}
              disabled={loading}
            >
              Cancel
            </button>
            <button 
              type="submit" 
              className="btn btn-success"
              disabled={loading}
            >
              {loading ? 'Completing...' : 'Complete Task'}
            </button>
          </div>
        </form>
      </div>
    </Modal>
  );
};

export default CompletionModal;
