import React, { useState } from 'react';

const TaskForm = ({ workerId, workerEmail, onTaskAssigned }) => {
  const [task, setTask] = useState('');
  const [instructions, setInstructions] = useState('');
  const [deadline, setDeadline] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/work/assign', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          workerId,
          task,
          instructions,
          deadline
        })
      });

      if (response.ok) {
        alert('Task assigned successfully and email notification sent!');
        setTask('');
        setInstructions('');
        setDeadline('');
        if (onTaskAssigned) {
          onTaskAssigned();
        }
      } else {
        const error = await response.json();
        alert('Error assigning task: ' + error.message);
      }
    } catch (error) {
      console.error('Error assigning task:', error);
      alert('Error assigning task: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Set minimum date to today
  const today = new Date().toISOString().split('T')[0];

  return (
    <form onSubmit={handleSubmit} className="mt-3">
      <div className="mb-3">
        <label className="form-label">What to do:</label>
        <input 
          type="text" 
          className="form-control" 
          value={task} 
          onChange={e => setTask(e.target.value)} 
          placeholder="Brief description of the task..."
          required 
          disabled={loading}
        />
      </div>
      
      <div className="mb-3">
        <label className="form-label">How to do it (Instructions):</label>
        <textarea 
          className="form-control" 
          rows="3"
          value={instructions} 
          onChange={e => setInstructions(e.target.value)} 
          placeholder="Detailed instructions on how to complete the task..."
          required 
          disabled={loading}
        />
      </div>
      
      <div className="mb-3">
        <label className="form-label">Deadline:</label>
        <input 
          type="date" 
          className="form-control" 
          value={deadline} 
          onChange={e => setDeadline(e.target.value)} 
          min={today}
          required 
          disabled={loading}
        />
      </div>
      
      <div className="d-flex gap-2">
        <button 
          type="submit" 
          className="btn btn-success"
          disabled={loading}
        >
          {loading ? 'Assigning...' : 'Assign Task'}
        </button>
      </div>
    </form>
  );
};

export default TaskForm;
