import React, { useState, useEffect } from 'react';
import CompletionModal from './CompletionModal';
import TaskDetailModal from './TaskDetailModal';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [detailTask, setDetailTask] = useState(null);
  const [hoveredTask, setHoveredTask] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/work`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setTasks(data);
      } else {
        console.error('Error fetching tasks');
        alert('Error fetching tasks');
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
      alert('Error fetching tasks: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleCompleteClick = (task, e) => {
    e.stopPropagation();
    setSelectedTask(task);
  };

  const handleTaskClick = (task) => {
    setDetailTask(task);
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const handleCloseDetailModal = () => {
    setDetailTask(null);
  };

  const refreshTasks = () => {
    fetchTasks();
  };

  const formatDeadline = (deadline) => {
    const date = new Date(deadline);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    let urgencyClass = '';
    let urgencyText = '';
    
    if (diffDays < 0) {
      urgencyClass = 'text-red-500';
      urgencyText = ' (OVERDUE)';
    } else if (diffDays <= 1) {
      urgencyClass = 'text-yellow-500';
      urgencyText = ' (DUE SOON)';
    }
    
    return {
      formatted: date.toLocaleDateString() + ' at ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}),
      urgencyClass,
      urgencyText
    };
  };

  const getTaskStatusInfo = (task) => {
    switch (task.status) {
      case 'rejected':
        return {
          badge: 'bg-red-500 text-white',
          text: 'Needs Revision',
          canComplete: true
        };
      case 'submitted':
        return {
          badge: 'bg-yellow-500 text-white',
          text: 'Under Review',
          canComplete: false
        };
      case 'approved':
        return {
          badge: 'bg-green-500 text-white',
          text: 'Approved',
          canComplete: false
        };
      default:
        return {
          badge: '',
          text: '',
          canComplete: true
        };
    }
  };

  // Separate tasks by status
  const pendingTasks = tasks.filter(task => 
    task.status === 'pending' || task.status === 'rejected'
  );
  const completedTasks = tasks.filter(task => 
    task.status === 'submitted' || task.status === 'approved'
  );

  const TaskCard = ({ task, showCompleteButton = true }) => {
    const deadlineInfo = formatDeadline(task.deadline);
    const isHovered = hoveredTask === task.id;
    const statusInfo = getTaskStatusInfo(task);

    return (
      <div 
        className="bg-white rounded-lg shadow-md p-4 mb-3 cursor-pointer hover:shadow-lg transition-shadow relative group border-l-4 border-blue-500"
        onMouseEnter={() => setHoveredTask(task.id)}
        onMouseLeave={() => setHoveredTask(null)}
        onClick={() => handleTaskClick(task)}
      >
        <div className="flex items-center justify-between">
          <div className="flex items-center flex-1">
            {/* Complete Circle Button */}
            {showCompleteButton && statusInfo.canComplete && (
              <div 
                className={`w-6 h-6 rounded-full border-2 border-gray-300 mr-3 flex items-center justify-center cursor-pointer transition-all ${
                  isHovered ? 'border-green-500 bg-green-50' : ''
                }`}
                onClick={(e) => handleCompleteClick(task, e)}
              >
                {isHovered && (
                  <svg className="w-4 h-4 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                )}
              </div>
            )}
            
            {/* Task Title */}
            <div className="flex-1">
              <h3 className="text-gray-800 font-medium text-sm leading-tight mb-1">
                {task.task}
              </h3>
              <div className={`text-xs ${deadlineInfo.urgencyClass || 'text-gray-500'}`}>
                Due: {deadlineInfo.formatted}{deadlineInfo.urgencyText}
              </div>
            </div>
          </div>

          {/* Status Badge */}
          {statusInfo.badge && (
            <span className={`px-2 py-1 rounded-full text-xs font-medium ${statusInfo.badge}`}>
              {statusInfo.text}
            </span>
          )}
        </div>

        {/* Rejection Feedback */}
        {task.status === 'rejected' && task.reviewNotes && (
          <div className="mt-3 p-2 bg-red-50 border border-red-200 rounded">
            <h4 className="text-red-700 font-medium text-xs mb-1 flex items-center">
              <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
              Admin Feedback:
            </h4>
            <p className="text-red-600 text-xs">{task.reviewNotes}</p>
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mb-4"></div>
          <div className="text-gray-600">Loading tasks...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          
          {/* Left Block - Pending Work */}
          <div className="bg-white rounded-xl shadow-sm border p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-gray-800 flex items-center">
                <svg className="w-5 h-5 mr-2 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                </svg>
                Active Tasks
              </h2>
              <div className="flex items-center space-x-3">
                <span className="text-sm text-gray-500">{pendingTasks.length} tasks</span>
                <button 
                  onClick={refreshTasks}
                  className="p-2 text-gray-400 hover:text-gray-600 transition-colors"
                  title="Refresh tasks"
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                  </svg>
                </button>
              </div>
            </div>
            
            {pendingTasks.length === 0 ? (
              <div className="text-center py-12">
                <svg className="w-16 h-16 text-gray-300 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <p className="text-gray-500 font-medium">No active tasks!</p>
                <p className="text-gray-400 text-sm">You're all caught up!</p>
              </div>
            ) : (
              <div className="space-y-0">
                {pendingTasks.map(task => (
                  <TaskCard key={task.id} task={task} showCompleteButton={true} />
                ))}
              </div>
            )}
          </div>

          {/* Right Block - Completed/Submitted Work */}
          <div className="bg-white rounded-xl shadow-sm border p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-gray-800 flex items-center">
                <svg className="w-5 h-5 mr-2 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                Completed Tasks
              </h2>
              <span className="text-sm text-gray-500">{completedTasks.length} tasks</span>
            </div>
            
            {completedTasks.length === 0 ? (
              <div className="text-center py-12">
                <svg className="w-16 h-16 text-gray-300 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
                <p className="text-gray-500 font-medium">No completed tasks yet</p>
                <p className="text-gray-400 text-sm">Finished tasks will appear here</p>
              </div>
            ) : (
              <div className="space-y-0">
                {completedTasks.map(task => (
                  <TaskCard key={task.id} task={task} showCompleteButton={false} />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
      
      {/* Completion Modal */}
      {selectedTask && (
        <CompletionModal 
          task={selectedTask} 
          onClose={handleCloseModal} 
          onUpdate={refreshTasks} 
        />
      )}

      {/* Task Detail Modal */}
      {detailTask && (
        <TaskDetailModal 
          task={detailTask} 
          onClose={handleCloseDetailModal} 
        />
      )}
    </div>
  );
};

export default TodoList;
