import React, { useState, useEffect } from 'react';
import CompletionModal from './CompletionModal';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [expandedTask, setExpandedTask] = useState(null);
  const [loading, setLoading] = useState(true);
  const [hoveredTask, setHoveredTask] = useState(null);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/work`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setTasks(data);
      } else {
        console.error('Error fetching tasks');
        alert('Error fetching tasks');
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
      alert('Error fetching tasks: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleCompleteClick = (task, e) => {
    e.stopPropagation();
    setSelectedTask(task);
  };

  const handleTaskClick = (task) => {
    if (expandedTask === task.id) {
      setExpandedTask(null);
    } else {
      setExpandedTask(task.id);
    }
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const refreshTasks = () => {
    fetchTasks();
  };

  const formatDeadline = (deadline) => {
    const date = new Date(deadline);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    let urgencyClass = '';
    let urgencyText = '';
    
    if (diffDays < 0) {
      urgencyClass = 'text-red-600';
      urgencyText = ' (OVERDUE)';
    } else if (diffDays <= 1) {
      urgencyClass = 'text-yellow-600';
      urgencyText = ' (DUE SOON)';
    }
    
    return {
      formatted: date.toLocaleDateString() + ' at ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}),
      urgencyClass,
      urgencyText
    };
  };

  const getTaskStatusBadge = (task) => {
    if (task.status === 'rejected') {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
          Needs Revision
        </span>
      );
    }
    return null;
  };

  // Separate tasks by status
  const incompleteTasks = tasks.filter(task => 
    task.status === 'pending' || task.status === 'rejected'
  );
  const underReviewTasks = tasks.filter(task => 
    task.status === 'submitted'
  );

  const TaskCard = ({ task, showCompleteButton = true }) => {
    const deadlineInfo = formatDeadline(task.deadline);
    const isExpanded = expandedTask === task.id;
    const isHovered = hoveredTask === task.id;

    return (
      <div 
        className="bg-gray-800 rounded-lg p-3 mb-2 cursor-pointer hover:bg-gray-700 transition-colors relative group"
        onMouseEnter={() => setHoveredTask(task.id)}
        onMouseLeave={() => setHoveredTask(null)}
        onClick={() => handleTaskClick(task)}
      >
        {/* Task Title */}
        <div className="flex items-center justify-between">
          <h3 className="text-white font-medium text-sm leading-tight">
            {task.task}
          </h3>
          {getTaskStatusBadge(task)}
        </div>

        {/* Hover Menu */}
        {isHovered && showCompleteButton && (
          <div className="absolute top-2 right-2 bg-gray-900 rounded shadow-lg border border-gray-600 z-10">
            <button
              onClick={(e) => handleCompleteClick(task, e)}
              className="flex items-center px-3 py-2 text-xs text-white hover:bg-gray-700 rounded transition-colors"
            >
              <svg className="w-3 h-3 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
              </svg>
              {task.status === 'rejected' ? 'Resubmit Work' : 'Mark Complete'}
            </button>
          </div>
        )}

        {/* Expanded Content */}
        {isExpanded && (
          <div className="mt-3 pt-3 border-t border-gray-600">
            {task.description && (
              <div className="mb-3">
                <h4 className="text-yellow-400 font-medium text-xs mb-1">Description:</h4>
                <p className="text-gray-300 text-xs leading-relaxed">{task.description}</p>
              </div>
            )}
            
            <div className="mb-3">
              <h4 className="text-yellow-400 font-medium text-xs mb-1">Instructions:</h4>
              <p className="text-gray-300 text-xs leading-relaxed">{task.instructions}</p>
            </div>
            
            <div className={`mb-3 ${deadlineInfo.urgencyClass}`}>
              <h4 className="text-yellow-400 font-medium text-xs mb-1">Deadline:</h4>
              <p className="text-xs">
                {deadlineInfo.formatted}{deadlineInfo.urgencyText}
              </p>
            </div>

            {task.status === 'rejected' && task.reviewNotes && (
              <div className="mb-3 p-2 bg-red-900/30 border border-red-500 rounded">
                <h4 className="text-red-400 font-medium text-xs mb-1 flex items-center">
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                  </svg>
                  Admin Feedback:
                </h4>
                <p className="text-red-300 text-xs">{task.reviewNotes}</p>
              </div>
            )}
            
            {task.assignedByUser && (
              <div className="text-gray-400 text-xs">
                Assigned by: {task.assignedByUser.name}
              </div>
            )}
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-4">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-white mb-4"></div>
          <div className="text-white">Loading tasks...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          
          {/* Today - Incomplete Work */}
          <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-4">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-white font-semibold text-lg">Today</h2>
              <div className="flex items-center space-x-2">
                <span className="text-gray-400 text-sm">{incompleteTasks.length}</span>
                <button className="text-gray-400 hover:text-white transition-colors">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                  </svg>
                </button>
              </div>
            </div>
            
            {incompleteTasks.length === 0 ? (
              <div className="text-center py-8">
                <svg className="w-12 h-12 text-gray-600 mx-auto mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <p className="text-gray-400">No pending tasks!</p>
                <p className="text-gray-500 text-sm">You're all caught up!</p>
              </div>
            ) : (
              <div className="space-y-2">
                {incompleteTasks.map(task => (
                  <TaskCard key={task.id} task={task} showCompleteButton={true} />
                ))}
                
                <button 
                  onClick={refreshTasks}
                  className="w-full mt-4 px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-700 rounded-lg transition-colors flex items-center justify-center"
                >
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                  Add a card
                </button>
              </div>
            )}
          </div>

          {/* This Week - Under Review */}
          <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-4">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-white font-semibold text-lg">This Week</h2>
              <div className="flex items-center space-x-2">
                <span className="text-gray-400 text-sm">{underReviewTasks.length}</span>
                <button className="text-gray-400 hover:text-white transition-colors">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                  </svg>
                </button>
              </div>
            </div>
            
            {underReviewTasks.length === 0 ? (
              <button 
                className="w-full p-3 border-2 border-dashed border-gray-600 rounded-lg text-gray-400 hover:border-gray-500 hover:text-gray-300 transition-colors flex items-center justify-center"
                onClick={() => {/* Add card functionality */}}
              >
                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
                Add a card
              </button>
            ) : (
              <div className="space-y-2">
                {underReviewTasks.map(task => (
                  <div key={task.id} className="relative">
                    <TaskCard task={task} showCompleteButton={false} />
                    <div className="absolute top-2 right-2">
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                        Under Review
                      </span>
                    </div>
                  </div>
                ))}
                
                <button 
                  className="w-full mt-4 p-3 border-2 border-dashed border-gray-600 rounded-lg text-gray-400 hover:border-gray-500 hover:text-gray-300 transition-colors flex items-center justify-center"
                  onClick={() => {/* Add card functionality */}}
                >
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                  Add a card
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      
      {selectedTask && (
        <CompletionModal 
          task={selectedTask} 
          onClose={handleCloseModal} 
          onUpdate={refreshTasks} 
        />
      )}
    </div>
  );
};

export default TodoList;
