import React from 'react';

const TaskDetailModal = ({ task, onClose }) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' at ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
  };

  const getDeadlineStatus = (deadline) => {
    const date = new Date(deadline);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays < 0) {
      return { class: 'text-red-300 bg-red-900', text: 'OVERDUE' };
    } else if (diffDays <= 1) {
      return { class: 'text-yellow-300 bg-yellow-900', text: 'DUE SOON' };
    } else {
      return { class: 'text-green-300 bg-green-900', text: 'ON TIME' };
    }
  };

  // Sample task data for demonstration
  const sampleTask = task || {
    task: "Complete project documentation",
    description: "Write comprehensive documentation for the new feature including API endpoints, usage examples, and troubleshooting guide.",
    instructions: "Follow the company style guide. Include code examples and screenshots where applicable. Review with team lead before submission.",
    assignedAt: "2025-01-15T10:00:00Z",
    assignedByUser: { name: "John Manager" },
    deadline: "2025-01-20T17:00:00Z",
    status: 'pending'
  };

  const deadlineStatus = getDeadlineStatus(sampleTask.deadline);

  return (
    <div 
      className="fixed top-0 left-0 w-screen h-screen bg-black bg-opacity-80 z-[9999]"
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '16px',
        zIndex: 9999
      }}
    >
      <div 
        className="bg-gray-800 rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-auto"
        style={{
          backgroundColor: '#1f2937',
          borderRadius: '12px',
          maxWidth: '48rem',
          maxHeight: '90vh',
          width: '100%',
          overflow: 'auto',
          boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.5)'
        }}
      >
        {/* Header */}
        <div className="px-6 py-4 border-b border-gray-600">
          <h2 className="text-xl font-semibold text-white">task details</h2>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6">
          {/* Title */}
          <div>
            <label className="block text-sm font-medium text-gray-200 mb-2">title</label>
            <div className="border-2 border-white p-3 bg-gray-700">
              <div className="text-white">{sampleTask.task}</div>
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-gray-200 mb-2">description</label>
            <div className="border-2 border-white p-3 bg-gray-700 min-h-[100px]">
              <div className="text-white whitespace-pre-wrap">
                {sampleTask.description || 'No description provided'}
              </div>
            </div>
          </div>

          {/* Instructions */}
          <div>
            <label className="block text-sm font-medium text-gray-200 mb-2">instructions</label>
            <div className="border-2 border-white p-3 bg-gray-700 min-h-[100px]">
              <div className="text-white whitespace-pre-wrap">
                {sampleTask.instructions || 'No instructions provided'}
              </div>
            </div>
          </div>

          {/* Assigned and Deadline Row */}
          <div className="flex space-x-8">
            {/* Assigned Column */}
            <div className="flex-1">
              <div className="text-sm font-medium text-gray-200 mb-1">assigned</div>
              <div className="text-sm text-gray-300">
                <div>{formatDate(sampleTask.assignedAt)}</div>
                {sampleTask.assignedByUser && (
                  <div>By: {sampleTask.assignedByUser.name}</div>
                )}
              </div>
            </div>
            
            {/* Deadline Column */}
            <div className="flex-1">
              <div className="text-sm font-medium text-gray-200 mb-1">deadline</div>
              <div className="text-sm text-gray-300">
                <div>{formatDate(sampleTask.deadline)}</div>
                <div className="text-xs">({deadlineStatus.text})</div>
              </div>
            </div>
          </div>

          {/* Status-specific information */}
          {sampleTask.status === 'rejected' && sampleTask.reviewNotes && (
            <div className="bg-red-900 border border-red-700 rounded-lg p-4">
              <h4 className="text-sm font-semibold text-red-300 mb-2">
                Admin Feedback - Revision Required
              </h4>
              <p className="text-red-200 whitespace-pre-wrap">{sampleTask.reviewNotes}</p>
            </div>
          )}

          {sampleTask.status === 'submitted' && (
            <div className="bg-yellow-900 border border-yellow-700 rounded-lg p-4">
              <p className="font-medium text-yellow-200">Work Submitted for Review</p>
              <p className="text-sm text-yellow-300">Your work is currently being reviewed by an administrator.</p>
            </div>
          )}

          {sampleTask.status === 'approved' && (
            <div className="bg-green-900 border border-green-700 rounded-lg p-4">
              <p className="font-medium text-green-200">Work Approved!</p>
              <p className="text-sm text-green-300">Great job! Your work has been approved.</p>
              {sampleTask.reviewNotes && (
                <p className="text-sm text-green-300 mt-1">Note: {sampleTask.reviewNotes}</p>
              )}
            </div>
          )}

          {/* Work submission details if available */}
          {(sampleTask.explanation || sampleTask.workLink) && (
            <div className="bg-gray-700 border border-gray-600 rounded-lg p-4">
              <h4 className="text-sm font-semibold text-gray-200 mb-2">
                Your Submission
              </h4>
              {sampleTask.explanation && (
                <div className="mb-3">
                  <p className="text-xs text-gray-400 mb-1">Work Notes:</p>
                  <p className="text-gray-200 text-sm whitespace-pre-wrap">{sampleTask.explanation}</p>
                </div>
              )}
              {sampleTask.workLink && (
                <div>
                  <p className="text-xs text-gray-400 mb-1">Work Link:</p>
                  <a 
                    href={sampleTask.workLink} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-blue-400 hover:text-blue-300 text-sm"
                  >
                    {sampleTask.workLink}
                  </a>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex justify-end px-6 py-4 bg-gray-700 border-t border-gray-600">
          <button 
            onClick={onClose}
            className="px-4 py-2 text-sm font-medium text-gray-300 bg-gray-600 border border-gray-500 rounded-lg hover:bg-gray-500 transition-colors"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default TaskDetailModal;
