// Updated Login.js with proper token handling
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    console.log('Login attempt for:', email);
    console.log('API URL:', process.env.REACT_APP_API_URL);

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/login`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ email, password }),
      });

      console.log('Login response status:', response.status);

      if (response.ok) {
        const data = await response.json();
        console.log('Login successful, received data:', data);
        
        // Store the token
        if (data.token) {
          localStorage.setItem('token', data.token);
          console.log('Token stored in localStorage:', data.token.substring(0, 20) + '...');
          
          // Verify the token was stored
          const storedToken = localStorage.getItem('token');
          console.log('Token verification - stored correctly:', !!storedToken);
          
          // Navigate based on role
          if (data.user && data.user.role) {
            const route = data.user.role === 'admin' ? '/admin' : '/worker';
            console.log('Navigating to:', route);
            navigate(route);
          } else {
            setError('User role not found in response');
          }
        } else {
          setError('No token received from server');
        }
      } else {
        const errorData = await response.json();
        console.error('Login failed:', errorData);
        setError(errorData.message || 'Login failed');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Unable to connect to the server. Please check your internet connection.');
    } finally {
      setLoading(false);
    }
  };

  // Debug function to test API connectivity
  const testConnection = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/health`);
      const data = await response.json();
      console.log('Health check:', data);
      alert(`Server status: ${data.status}\nEnvironment: ${data.environment}`);
    } catch (error) {
      console.error('Health check failed:', error);
      alert('Cannot connect to server');
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h2 className="card-title">Login</h2>
              
              {/* Debug info */}
              <div className="mb-3 p-2 bg-light border rounded" style={{fontSize: '12px'}}>
                <strong>Debug Info:</strong><br/>
                API URL: {process.env.REACT_APP_API_URL}<br/>
                <button 
                  type="button" 
                  onClick={testConnection}
                  className="btn btn-sm btn-outline-secondary mt-1"
                >
                  Test Server Connection
                </button>
              </div>

              <form onSubmit={handleLogin}>
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Enter email"
                    required
                    disabled={loading}
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter password"
                    required
                    disabled={loading}
                  />
                </div>
                {error && <div className="alert alert-danger">{error}</div>}
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Logging in...' : 'Login'}
                </button>
              </form>
              
              <div className="mt-3 text-muted small">
                <p>Default admin login:</p>
                <p>Email: admin@taskpilot.com</p>
                <p>Password: admin123</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
