// WorkerDashboard.js
import React, { useState, useEffect } from 'react';
import TodoList from '../components/TodoList';
import { useNavigate } from 'react-router-dom';

const WorkerDashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/');
          return;
        }
        const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/verify`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        if (response.ok) {
          const data = await response.json();
          setUser(data.user);
          if (!data.user.googleAccessToken) {
            const connect = window.confirm('Connect your Google Calendar to sync tasks. Proceed?');
            if (connect) {
              window.location.href = `${process.env.REACT_APP_API_URL}/auth/google`;
            }
          }
        } else {
          localStorage.removeItem('token');
          navigate('/');
        }
      } catch (error) {
        console.error('Error fetching user:', error);
        localStorage.removeItem('token');
        navigate('/');
      }
    };
    fetchUser();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  if (!user) {
    return <div style={{ color: '#ffffff', textAlign: 'center', backgroundColor: '#333333' }}>Loading...</div>;
  }

  return (
    <div className="container-fluid" style={{ backgroundColor: '#333333' }}>
      <nav className="navbar navbar-light bg-light border-bottom mb-4">
        <div className="container-fluid">
          <div>
            <span className="navbar-brand mb-0 h1">Worker Dashboard</span>
            <small className="text-muted d-block">
              Welcome, {user.name} ({user.email})
            </small>
          </div>
          <button 
            className="btn btn-outline-danger" 
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </nav>
      <TodoList />
    </div>
  );
};

export default WorkerDashboard;
