import React, { useState } from 'react';
import { db, addDoc, collection } from '../services/firebase';
import emailjs from 'emailjs-com';

const TaskForm = ({ workerId, workerEmail }) => {
  const [what, setWhat] = useState('');
  const [how, setHow] = useState('');
  const [deadline, setDeadline] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, 'tasks'), {
        assignedTo: workerId,
        what,
        how,
        deadline,
        status: 'pending',
        explanation: '',
        link: '',
        completedAt: null
      });
      // Send email via EmailJS
      emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', {
        to_email: workerEmail,
        message: 'You have new work assigned. Please log in to view.'
      }, 'YOUR_USER_ID');
      alert('Task assigned and email sent!');
      setWhat('');
      setHow('');
      setDeadline('');
    } catch (error) {
      console.error('Error assigning task:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mt-3">
      <div className="mb-3">
        <label>What to do:</label>
        <input type="text" className="form-control" value={what} onChange={e => setWhat(e.target.value)} required />
      </div>
      <div className="mb-3">
        <label>How to do it:</label>
        <textarea className="form-control" value={how} onChange={e => setHow(e.target.value)} required />
      </div>
      <div className="mb-3">
        <label>Deadline:</label>
        <input type="date" className="form-control" value={deadline} onChange={e => setDeadline(e.target.value)} required />
      </div>
      <button type="submit" className="btn btn-success">Send</button>
    </form>
  );
};

export default TaskForm;
