import React, { useState, useEffect } from 'react';
import CompletionModal from './CompletionModal';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/work`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        const activeTasks = data.filter(task => 
          task.status === 'pending' || task.status === 'rejected'
        );
        setTasks(activeTasks);
      } else {
        console.error('Error fetching tasks');
        alert('Error fetching tasks');
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
      alert('Error fetching tasks: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleClick = (task) => {
    setSelectedTask(task);
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const refreshTasks = () => {
    fetchTasks();
  };

  const formatDeadline = (deadline) => {
    const date = new Date(deadline);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    let urgencyClass = '';
    let urgencyText = '';
    
    if (diffDays < 0) {
      urgencyClass = 'text-danger';
      urgencyText = ' (OVERDUE)';
    } else if (diffDays <= 1) {
      urgencyClass = 'text-warning';
      urgencyText = ' (DUE SOON)';
    }
    
    return {
      formatted: date.toLocaleDateString() + ' at ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}),
      urgencyClass,
      urgencyText
    };
  };

  const getTaskStatusBadge = (task) => {
    if (task.status === 'rejected') {
      return (
        <span className="badge bg-danger">
          Needs Revision
        </span>
      );
    }
    return null;
  };

  if (loading) {
    return (
      <div className="p-3">
        <h2 className="text-gold">Your To-Do List</h2>
        <div className="text-center">
          <div className="spinner-border text-gold" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <div className="mt-2">Loading tasks...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-3">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2 className="text-gold">Your To-Do List</h2>
      </div>
      {tasks.length === 0 ? (
        <div className="alert alert-info">
          <i className="bi bi-info-circle me-2"></i>
          No pending tasks! You're all caught up!
        </div>
      ) : (
        <div className="row">
          <div className="col-12">
            <div className="mb-3">
              <small className="text-muted">You have {tasks.length} pending task{tasks.length !== 1 ? 's' : ''}</small>
            </div>
            <div className="list-group">
              {tasks.map(task => {
                const deadlineInfo = formatDeadline(task.deadline);
                return (
                  <div key={task.id} className="list-group-item">
                    <div className="d-flex justify-content-between align-items-start">
                      <div className="flex-grow-1">
                        <div className="d-flex justify-content-between align-items-center mb-2">
                          <h5 className="mb-0 text-gold">{task.task}</h5>
                          {getTaskStatusBadge(task)}
                        </div>
                        
                        {task.description && (
                          <div className="mb-2">
                            <strong className="text-gold">Description:</strong>
                            <div className="mt-1 p-2 bg-light rounded">
                              {task.description}
                            </div>
                          </div>
                        )}
                        
                        <div className={`mb-2 ${deadlineInfo.urgencyClass}`}>
                          <i className="bi bi-calendar-event me-1"></i>
                          <strong>Deadline:</strong> {deadlineInfo.formatted}{deadlineInfo.urgencyText}
                        </div>
                        
                        {task.instructions && (
                          <div className="mb-2">
                            <strong className="text-gold">Instructions:</strong>
                            <div className="mt-1 p-2 bg-light rounded">
                              {task.instructions}
                            </div>
                          </div>
                        )}
                        
                        {task.status === 'rejected' && task.reviewNotes && (
                          <div className="mb-2 p-3 border border-danger rounded">
                            <strong className="text-danger">
                              <i className="bi bi-exclamation-triangle me-1"></i>
                              Admin Feedback (Revision Required):
                            </strong>
                            <div className="mt-2 text-danger">
                              {task.reviewNotes}
                            </div>
                          </div>
                        )}
                        
                        {task.assignedByUser && (
                          <div className="text-muted">
                            <small>Assigned by: {task.assignedByUser.name}</small>
                          </div>
                        )}
                      </div>
                      <div className="ms-3">
                        <button 
                          className={`btn ${task.status === 'rejected' ? 'btn-warning' : 'btn-primary'}`}
                          onClick={() => handleClick(task)}
                        >
                          <i className={`bi ${task.status === 'rejected' ? 'bi-arrow-repeat' : 'bi-check-circle'} me-1`}></i>
                          {task.status === 'rejected' ? 'Resubmit Work' : 'Mark Complete'}
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      )}
      {selectedTask && (
        <CompletionModal 
          task={selectedTask} 
          onClose={handleCloseModal} 
          onUpdate={refreshTasks} 
        />
      )}
    </div>
  );
};

export default TodoList;
