import React, { useState } from 'react';
import Modal from 'react-modal';
import { db, doc, updateDoc } from '../services/firebase';

Modal.setAppElement('#root');

const CompletionModal = ({ task, onClose, onUpdate }) => {
  const [explanation, setExplanation] = useState('');
  const [link, setLink] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const taskRef = doc(db, 'tasks', task.id);
      await updateDoc(taskRef, {
        status: 'completed',
        explanation,
        link,
        completedAt: new Date().toISOString()
      });
      onUpdate();
      onClose();
    } catch (error) {
      console.error('Error completing task:', error);
    }
  };

  return (
    <Modal isOpen={true} onRequestClose={onClose} style={{content: {top: '50%', left: '50%', right: 'auto', bottom: 'auto', marginRight: '-50%', transform: 'translate(-50%, -50%)'}}}>
      <h2>Complete Task</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Explanation (if done differently):</label>
          <textarea className="form-control" value={explanation} onChange={e => setExplanation(e.target.value)} />
        </div>
        <div className="mb-3">
          <label>Link to work:</label>
          <input type="url" className="form-control" value={link} onChange={e => setLink(e.target.value)} />
        </div>
        <button type="submit" className="btn btn-primary">Done</button>
        <button type="button" className="btn btn-secondary ms-2" onClick={onClose}>Cancel</button>
      </form>
    </Modal>
  );
};

export default CompletionModal;
