// TodoList.js - Updated with better authentication error handling
import React, { useState, useEffect } from 'react';
import CompletionModal from './CompletionModal';
import TaskDetailModal from './TaskDetailModal';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [detailTask, setDetailTask] = useState(null);
  const [hoveredTask, setHoveredTask] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        setError('No authentication token found');
        return;
      }

      console.log('Fetching tasks with token:', token ? 'Present' : 'Missing');
      console.log('API URL:', process.env.REACT_APP_API_URL);

      const response = await fetch(`${process.env.REACT_APP_API_URL}/work`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });

      console.log('Fetch tasks response status:', response.status);

      if (response.ok) {
        const data = await response.json();
        console.log('Tasks fetched successfully:', data.length, 'tasks');
        setTasks(data);
        setError(null);
      } else {
        const errorData = await response.json().catch(() => ({}));
        console.error('Fetch tasks failed:', response.status, errorData);
        
        if (response.status === 401 || response.status === 403) {
          setError(`Authentication failed (${response.status}): ${errorData.message || 'Please login again'}`);
          // Don't auto-redirect, let parent component handle this
        } else {
          setError(`Error fetching tasks (${response.status}): ${errorData.message || response.statusText}`);
        }
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setError(`Network error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleTaskClick = (task) => {
    setDetailTask(task);
  };

  const handleMarkDone = (e, task) => {
    e.stopPropagation();
    setSelectedTask(task);
  };

  const handleBubbleClick = (e, task) => {
    e.stopPropagation();
    setSelectedTask(task);
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const handleCloseDetailModal = () => {
    setDetailTask(null);
  };

  const refreshTasks = () => {
    setLoading(true);
    setError(null);
    fetchTasks();
  };

  const formatDeadline = (deadline) => {
    const date = new Date(deadline);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    let urgencyClass = '';
    let urgencyText = '';
    
    if (diffDays < 0) {
      urgencyClass = 'text-red-400';
      urgencyText = ' (OVERDUE)';
    } else if (diffDays <= 1) {
      urgencyClass = 'text-yellow-400';
      urgencyText = ' (DUE SOON)';
    } else {
      urgencyClass = 'text-green-400';
    }
    
    return {
      formatted: date.toLocaleDateString() + ' at ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}),
      urgencyClass,
      urgencyText
    };
  };

  const pendingTasks = tasks.filter(task => 
    task.status === 'pending' || task.status === 'rejected'
  );
  const completedTasks = tasks.filter(task => 
    task.status === 'submitted' || task.status === 'approved'
  );

  const TaskCard = ({ task, showBubble = true }) => {
    const deadlineInfo = formatDeadline(task.deadline);
    const isHovered = hoveredTask === task.id;

    return (
      <div 
        className="relative bg-gray-800 rounded-lg p-4 mb-3 cursor-pointer hover:bg-gray-700 transition-colors border border-gray-600"
        onMouseEnter={() => setHoveredTask(task.id)}
        onMouseLeave={() => setHoveredTask(null)}
        onClick={() => handleTaskClick(task)}
      >
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-base font-medium text-white mb-1">{task.task}</h3>
            <p className={`text-sm ${deadlineInfo.urgencyClass}`}>
              <span className="font-medium">Due:</span> {deadlineInfo.formatted}{deadlineInfo.urgencyText}
            </p>
            {task.status === 'rejected' && (
              <p className="text-sm text-red-400 mt-1">
                Rejected: {task.reviewNotes || 'No feedback provided'}
              </p>
            )}
          </div>
          {showBubble && (
            <div 
              className={`w-8 h-8 rounded-full flex items-center justify-center transition-opacity duration-200 ${
                isHovered ? 'opacity-100' : 'opacity-0'
              }`}
              style={{ backgroundColor: isHovered ? '#10B981' : '#6B7280' }}
              onClick={(e) => handleBubbleClick(e, task)}
            >
              <svg
                className="w-5 h-5 text-white"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M5 13l4 4L19 7"
                />
              </svg>
            </div>
          )}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="p-6">
        <h2 className="text-xl font-semibold text-white mb-4">Work To Be Done</h2>
        <div className="text-center">
          <div className="spinner-border text-teal-500" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <div className="mt-2 text-white">Loading tasks...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <h2 className="text-xl font-semibold text-white mb-4">Work To Be Done</h2>
        <div className="bg-red-900 border border-red-600 rounded-lg p-4 mb-4">
          <h3 className="text-red-200 font-semibold mb-2">Error Loading Tasks</h3>
          <p className="text-red-300 mb-3">{error}</p>
          <div className="flex gap-2">
            <button 
              onClick={refreshTasks}
              className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
            >
              Retry
            </button>
            <button 
              onClick={() => {
                localStorage.removeItem('token');
                window.location.reload();
              }}
              className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700"
            >
              Logout & Refresh
            </button>
          </div>
          
          {/* Debug info */}
          <div className="mt-4 text-xs text-red-200">
            <p>Debug Info:</p>
            <p>API URL: {process.env.REACT_APP_API_URL}</p>
            <p>Token exists: {!!localStorage.getItem('token')}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex space-x-6">
        <div 
          className="bg-gray-900 rounded-xl shadow-lg border-2 border-teal-500 p-6" 
          style={{ width: '644px', height: '396px' }}
        >
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              Work To Be Done
            </h2>
            <div className="flex items-center space-x-3">
              <span className="text-sm text-gray-400">{pendingTasks.length} tasks</span>
              <button 
                onClick={refreshTasks}
                className="p-2 text-gray-400 hover:text-white transition-colors"
                title="Refresh tasks"
                disabled={loading}
              >
                ↻
              </button>
            </div>
          </div>
          <div className="h-72 overflow-y-auto">
            {pendingTasks.length === 0 ? (
              <div className="text-center py-12">
                <div className="w-16 h-16 text-gray-600 mx-auto mb-4 text-6xl">✅</div>
                <p className="text-gray-400 font-medium">No pending tasks!</p>
                <p className="text-gray-500 text-sm">You're all caught up!</p>
              </div>
            ) : (
              <div className="space-y-0">
                {pendingTasks.map(task => (
                  <TaskCard key={task.id} task={task} showBubble={true} />
                ))}
              </div>
            )}
          </div>
        </div>
        <div 
          className="bg-gray-900 rounded-xl shadow-lg border-2 border-teal-500 p-6" 
          style={{ width: '644px', height: '396px' }}
        >
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              Completed Work
            </h2>
            <span className="text-sm text-gray-400">{completedTasks.length} tasks</span>
          </div>
          <div className="h-72 overflow-y-auto">
            {completedTasks.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-gray-400 font-medium">No completed work yet</p>
                <p className="text-gray-500 text-sm">Finished tasks will appear here</p>
              </div>
            ) : (
              <div className="space-y-0">
                {completedTasks.map(task => (
                  <TaskCard key={task.id} task={task} showBubble={false} />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
      {selectedTask && (
        <CompletionModal 
          task={selectedTask} 
          onClose={handleCloseModal} 
          onUpdate={refreshTasks} 
        />
      )}
      {detailTask && (
        <TaskDetailModal 
          task={detailTask} 
          onClose={handleCloseDetailModal}
        />
      )}
    </div>
  );
};

export default TodoList;
