import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db, createUserWithEmailAndPassword, doc, setDoc } from '../services/firebase';

const CreateUser = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('default123'); // Temporary password
  const [role, setRole] = useState('worker');
  const navigate = useNavigate();

  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      await setDoc(doc(db, 'users', userCredential.user.uid), { email, role });
      alert('User created! Temporary password: ' + password);
      navigate('/admin');
    } catch (error) {
      console.error('Error creating user:', error);
      alert('Creation failed');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Create New User</h2>
      <form onSubmit={handleCreate}>
        <div className="mb-3">
          <label>Email:</label>
          <input type="email" className="form-control" value={email} onChange={e => setEmail(e.target.value)} required />
        </div>
        <div className="mb-3">
          <label>Role:</label>
          <select className="form-control" value={role} onChange={e => setRole(e.target.value)}>
            <option value="worker">Worker</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary">Create</button>
      </form>
    </div>
  );
};

export default CreateUser;
