import React, { useState } from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const CompletionModal = ({ task, onClose, onUpdate }) => {
  const [explanation, setExplanation] = useState('');
  const [workLink, setWorkLink] = useState('');
  const [status, setStatus] = useState('done');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/work/complete/${task.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          status,
          explanation: explanation || undefined,
          workLink: workLink || undefined
        })
      });

      if (response.ok) {
        onUpdate();
        onClose();
        alert('Work submitted for review successfully!');
      } else {
        const error = await response.json();
        alert('Error submitting work: ' + error.message);
      }
    } catch (error) {
      console.error('Error submitting work:', error);
      alert('Error submitting work: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const modalStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      width: '500px',
      maxWidth: '90%',
      maxHeight: '80%',
      overflow: 'auto',
      backgroundColor: '#2d2d2d',
      border: '2px solid #ffd700',
      borderRadius: '8px'
    },
    overlay: {
      backgroundColor: 'rgba(0, 0, 0, 0.75)'
    }
  };

  return (
    <Modal isOpen={true} onRequestClose={onClose} style={modalStyles}>
      <div>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h3 className="mb-0 text-gold">Submit Completed Work</h3>
          <button 
            type="button" 
            className="btn-close" 
            onClick={onClose}
            disabled={loading}
          ></button>
        </div>
        
        <div className="mb-3 p-3 bg-light rounded">
          <div className="mb-2">
            <strong className="text-gold">Task:</strong> {task.task}
          </div>
          {task.description && (
            <div className="mb-2">
              <strong className="text-gold">Description:</strong> {task.description}
            </div>
          )}
          {task.instructions && (
            <div className="mb-2">
              <strong className="text-gold">Instructions:</strong> {task.instructions}
            </div>
          )}
          <div>
            <strong className="text-gold">Deadline:</strong> {new Date(task.deadline).toLocaleDateString()} at {new Date(task.deadline).toLocaleTimeString()}
          </div>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Completion Status:</label>
            <select 
              className="form-control" 
              value={status} 
              onChange={e => setStatus(e.target.value)}
              disabled={loading}
            >
              <option value="done">Completed as requested</option>
              <option value="custom">Completed differently (explain below)</option>
            </select>
          </div>
          
          <div className="mb-3">
            <label className="form-label">
              {status === 'custom' ? 'Explanation (required - how did you complete it differently):' : 'Work Notes (optional):'}
            </label>
            <textarea 
              className="form-control" 
              rows="4"
              value={explanation} 
              onChange={e => setExplanation(e.target.value)}
              placeholder={status === 'custom' ? 
                "Explain how you completed the task differently from the instructions..." : 
                "Any notes about your completed work..."
              }
              required={status === 'custom'}
              disabled={loading}
            />
          </div>
          
          <div className="mb-3">
            <label className="form-label">Link to your work (optional):</label>
            <input 
              type="url" 
              className="form-control" 
              value={workLink} 
              onChange={e => setWorkLink(e.target.value)}
              placeholder="https://... (link to your completed work)"
              disabled={loading}
            />
          </div>
          
          <div className="alert alert-info">
            <i className="bi bi-info-circle me-2"></i>
            Your work will be submitted for admin review. You'll be notified once it's been reviewed and approved.
          </div>
          
          <div className="d-flex gap-2 justify-content-end">
            <button 
              type="button" 
              className="btn btn-secondary" 
              onClick={onClose}
              disabled={loading}
            >
              Cancel
            </button>
            <button 
              type="submit" 
              className="btn btn-success"
              disabled={loading}
            >
              {loading ? 'Submitting...' : 'Submit for Review'}
            </button>
          </div>
        </form>
      </div>
    </Modal>
  );
};

export default CompletionModal;
