import React, { useState, useEffect } from 'react';
import { db, query, collection, getDocs, where } from '../services/firebase';
import TaskForm from './TaskForm';

const WorkerList = () => {
  const [workers, setWorkers] = useState([]);
  const [expandedWorker, setExpandedWorker] = useState(null);

  useEffect(() => {
    const fetchWorkers = async () => {
      const q = query(collection(db, 'users'), where('role', '==', 'worker'));
      const querySnapshot = await getDocs(q);
      setWorkers(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };
    fetchWorkers();
  }, []);

  const handleExpand = (workerId) => {
    setExpandedWorker(expandedWorker === workerId ? null : workerId);
  };

  return (
    <div className="p-3">
      <h2>Workers</h2>
      <ul className="list-group">
        {workers.map(worker => (
          <li key={worker.id} className="list-group-item">
            {worker.email}
            <button className="btn btn-sm btn-primary ms-2" onClick={() => handleExpand(worker.id)}>Assign Task</button>
            {expandedWorker === worker.id && <TaskForm workerId={worker.id} workerEmail={worker.email} />}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WorkerList;
